class DashboardPage extends StatefulWidget {
  DashboardPage({Key? key, required this.title}) : super(key: key);

  final String title;

  @override
  State<DashboardPage> createState() => _DashboardPageState();
}

class _DashboardPageState extends State<DashboardPage> {
  final int bColor = 0xffeeeeee;

  final double bRad = 35.0;

  final int feedNum = 3;

  final int groupValue = 0;

  final String picture =
      "data:image/jpeg;base64,";

  List<String> waitingDocIDs = [];
  List<String> historyDocIDs = [];

  Future getReservationDocId() async {
    await FirebaseFirestore.instance
        .collection('reservation_waiting')
        .where('pending', isEqualTo: false)
        .where('prefer', isEqualTo: '치킨')
    // .where('reservation_pending', isEqualTo: false)
        .where('location', isEqualTo: '신촌')
        .get()
        .then((snapshot) => snapshot.docs.forEach((resDocument) {
      // print(resDocument.reference.id);
      waitingDocIDs.add(resDocument.reference.id);
    }));
  }

  Future getHistoryDocId() async {
    await FirebaseFirestore.instance
        .collection('reservation_confirm')
    // .orderBy('date')
        .where('name', isEqualTo: '백번치킨')
    // .where('prefer', isEqualTo: '치킨')
    // // .where('reservation_pending', isEqualTo: false)
    // .where('location', isEqualTo: '신촌')
        .get()
        .then((snapshot) => snapshot.docs.forEach((confDocument) {
      print(confDocument.reference.id);
      historyDocIDs.add(confDocument.reference.id);
    }));
  }

  @override
  void initState() {
    getReservationDocId();
    getHistoryDocId();
    super.initState();
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: PreferredSize(
        preferredSize: Size.fromHeight(60.0),
        child: ClipRRect(
          borderRadius: BorderRadius.only(
            bottomLeft: Radius.circular(bRad),
            bottomRight: Radius.circular(bRad),
          ),
          child: AppBar(
            backgroundColor: Color(bColor),
            title: Text(widget.title),
          ),
        ),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: [
            ///// INCOMING TEAM //////
            Stack(alignment: AlignmentDirectional.topStart, children: [
              SizedBox(
                height: 100,
                child: FutureBuilder(
                  future: getReservationDocId(),
                  builder: (context, snapshot) {
                    return ListView.builder(
                        itemCount: 1,
                        itemBuilder: (context, index) => ListTile(
                          title: GetReservations(
                            documentID: waitingDocIDs[index],
                            bColor: bColor,
                            bRad: bRad,
                          ),
                        ));
                  },
                ),
              ),
              Container(
                alignment: Alignment(-0.92, 2),
                child: CircleAvatar(
                  radius: 25,
                  backgroundColor: Colors.grey,
                  child: CircleAvatar(
                    radius: 23,
                    backgroundColor: Colors.white,
                    backgroundImage: NetworkImage(teamList[0]['image']!),
                  ),
                ),
              ),
              Container(
                  alignment: Alignment(0.5, 0),
                  child: FloatingActionButton(
                    onPressed: () {
                      DateTime now = DateTime.now();

                      String formattedDate =
                      DateFormat("yyyyMMddHHmmss").format(now);

                      int dateAsInteger = int.parse(formattedDate);
                      final data = {
                        'name': '백번치킨',
                        'time': '18:00',
                        'person': '10',
                        'user': 'test123@gmail.com',
                        'date': dateAsInteger
                      };
                      setState(() {
                        FirebaseFirestore.instance
                            .collection('reservation_confirm')
                            .add(data)
                            .then((documentSnapshot) => print(
                            "Added Data with ID: ${documentSnapshot.id}"));
                      });
                    },
                    child: Text('수락'),
                  ))
            ]),

            ///// RECEIVING SWITCH /////

            Container(
              margin: const EdgeInsets.all(8.0),
              // width: screenWidth,
              decoration: BoxDecoration(
                color: Color(bColor),
                borderRadius: BorderRadius.circular(bRad),
              ),
              child: Container(
                width: 800,
                padding: EdgeInsets.all(8.0),
                child: Column(
                  children: [
                    Icon(Icons.notifications),
                    Container(
                      width: MediaQuery.of(context).size.width,
                      padding: EdgeInsets.all(8.0),
                      child: GestureDetector(
                        child: CupertinoSlidingSegmentedControl<int>(
                          thumbColor: Colors.blue,
                          groupValue: groupValue,
                          children: {
                            0: Text("No"),
                            1: Text("Yes"),
                          },
                          onValueChanged: (int? newValue) {
                            if (newValue != null) {
                              showCustomDialog(context);
                            }
                          },
                        ),
                      ),
                    ),
                  ],
                ),
              ),
            ),
            Container(
              padding: EdgeInsets.only(left: 15.0),
              child: Align(
                alignment: AlignmentDirectional.topStart,
                child: Text("이전 예약"),
              ),
            ),

            /////// 예약확정 Order History //////////
            Expanded(
                child: FutureBuilder(
                    future: getHistoryDocId(),
                    builder: (context, index) {
                      return ListView.builder(
                        itemCount: feedNum,
                        itemBuilder: (context, index) => ListTile(
                          title: GetHistory(
                            documentID: historyDocIDs[index],
                            bColor: bColor,
                            bRad: bRad,
                          ),
                        ),
                      );
                    })),
          ],
        ),
      ),
    );
  }

  ///// DIALOG POPUP /////

  void showCustomDialog(BuildContext context) {
    showDialog(
      context: context,
      builder: (BuildContext mainBuildContext) {
        return FutureBuilder(
          future: simulateTask(),
          builder: (BuildContext context, AsyncSnapshot<void> snapshot) {
            if (snapshot.connectionState == ConnectionState.done) {
              // Task completed, close the original page's navigator stack
              Navigator.of(mainBuildContext).pop();
            }

            return AlertDialog(
              content: Column(
                mainAxisSize: MainAxisSize.min,
                children: [
                  Stack(
                    children: [
                      DecreasingLinearProgressIndicator(duration: 5),
                      Row(
                        mainAxisAlignment: MainAxisAlignment.center,
                        children: [
                          Container(
                              margin: EdgeInsets.only(right: 10),
                              child: Text('${teamList[0]['teamNo']!}명')),
                          Container(
                              margin: EdgeInsets.only(left: 10),
                              child: Text('${teamList[0]['teamHr']!}시'))
                        ],
                      ),
                    ],
                  ),
                ],
              ),
              actions: [
                TextButton(
                  onPressed: () {
                    // Close the dialog
                    // Navigator.of(context).pop();
                  },
                  child: Text('수락'),
                ),
                TextButton(
                  onPressed: () {
                    // Close the dialog
                    Navigator.of(context).pop();
                  },
                  child: Text('Close'),
                ),
              ],
            );
          },
        );
      },
    );
  }

  Future<void> simulateTask() async {
    // Simulate a task taking place
    await Future.delayed(Duration(seconds: 5));
  }
}

///// PROGRESS BAR /////

class DecreasingLinearProgressIndicator extends StatelessWidget {
  final double duration;

  const DecreasingLinearProgressIndicator({required this.duration});

  @override
  Widget build(BuildContext context) {
    return TweenAnimationBuilder<double>(
      tween: Tween(begin: 1.0, end: 0.0),
      duration: Duration(seconds: duration.toInt()),
      builder: (context, value, child) {
        return LinearProgressIndicator(
          borderRadius: BorderRadius.circular(30),
          value: value,
          minHeight: 20,
          backgroundColor: Color(0xffe0e0e1),
          valueColor: AlwaysStoppedAnimation<Color>(Colors.blue),
        );
      },
    );
  }
}
